package day35_Encapsulation;public class CydeoStudent {    public String name, fieldOfStudy;    public int age, batchNumber, groupNumber;    public char gender;    public static String schoolName, programmingLanguage, secretCode;    public CydeoStudent(String name, String fieldOfStudy, int age, int batchNumber, int groupNumber, char gender) {        this.name = name;        this.fieldOfStudy = fieldOfStudy;        this.age = age;        this.batchNumber = batchNumber;        this.groupNumber = groupNumber;        this.gender = gender;    }    static {        schoolName = "Cydeo";        programmingLanguage = "Java";        secretCode = "Wodeen Spoon";    }    public static void printSchoolName(){        System.out.println("schoolName = " + schoolName);    }    public static void secretCode(){        System.out.println("secretCode = " + secretCode);    }    public void study(){        System.out.println(name+" is studying.");    }    public String toString() {        return "CydeoStudent{" +                "name='" + name + '\'' +                ", gender=" + gender +                ", fieldOfStudy='" + fieldOfStudy + '\'' +                ", age=" + age +                ", batchNumber=" + batchNumber +                ", groupNumber=" + groupNumber +                ", schoolName=" + schoolName +                ", programming Language=" + programmingLanguage +                ", secretCode=" + secretCode +                '}';    }}/*2. create a class named CybertekStudent            Variables:                name, gender, age, batchNumber, groupNumber, schoolName, fieldOfStudy, programmingLanguage, secretCode            Add a constructor to initialize all the fields            Add a static block to initialize all the statics            methods:                printSchoolName();                printSecretCode();                attendClass():                study()                toString()*/