package day35_Encapsulation.carTask;public class BMW extends Car{    public BMW(String brand, String model, int year, double price, String color, int miles) {        super(brand, model, year, price, color, miles);    }    @Override    public void start() {        System.out.println("Call mechanic");        System.out.println("Jump start");    }    public void breaksDown(){        System.out.println(brand+" "+model+" is broken down");    }    public void racing(){        System.out.println(brand+" "+model+" is racing");    }/*2. BMW:start():		Call mechanic		Jump start					extra methods:						breaksDown()						racing()*/}