package day35_Encapsulation.carTask;public class Tesla extends Car{    public Tesla(String brand, String model, int year, double price, String color, int miles) {        super(brand, model, year, price, color, miles);    }    @Override    public void start() {        System.out.println("say \"Start\"");    }    public void autoPilot(){        System.out.println(brand+" "+model+" activated autopilot ");    }}/*3. Tesla:			start():		        say "Start"					extra methods:					 	autoPilot();*/