package day35_Encapsulation.carTask;public class Toyota extends Car {    public Toyota(String brand, String model, int year, double price, String color, int miles) {        super(brand, model, year, price, color, miles);    }    public void reliable() {        System.out.println(brand + " " + model + " is the one of most reliable car with " + color + "color");    }    @Override    public void start() {        System.out.println("Twist the key to Ignition");    }}/*1. Toyota:					extra methods:							reliabile()						Toyota extends Car:	start():		twist the key to Ignition							*/