package day35_Encapsulation.encapsulation;public class CircleObject {    public static void main(String[] args) {        Circle c1 = new Circle(4.1);        c1.calcArea();        c1.calcPerimeter();        System.out.println(c1);        c1.setRadius(10);        c1.calcArea();        c1.calcPerimeter();        System.out.println(c1);        System.out.println(c1.getRadius());    }}