package day36_Inheritance.employeeTask;public class Emloyee {    public String name;    public char gender;    public int age;    public String jobTitle;    public int id;public int salary;    public void setInfo(String name, char gender, int age, String jobTitle, int id,int salary) {        this.name = name;        this.gender = gender;        this.age = age;        this.jobTitle = jobTitle;        this.id = id;        this.salary=salary;    }    public void work() {        System.out.println(name + " is working");    }    public String toString() {        return "Emloyee{" +                "name='" + name + '\'' +                ", gender=" + gender +                ", age=" + age +                ", jobTitle='" + jobTitle + '\'' +                ", id=" + id +                ", salary=" + salary +                '}';    }}/*Employee:		name, gender, age, jobTitle, id, salary, companyName		setInfo(), work(), toString()	Tester	Developer	Driver	Teacher	Employee Task:	2.1 Create a class named Employee:			variables:				name, gender, age, id, jobTitle, salary			Methods:				setInfo()				work()				toString()*/