package day37_Inheritance.scrumTask;import java.util.Arrays;public class AmazonInc {    public static void main(String[] args) {        String company = "Amazon Inc";        ProductOwner PO = new ProductOwner("Suhaib", 29, 'M', 1, 160000, company);        BusinesAnalist BA = new BusinesAnalist("Cihad", 30, 'M', 2, 150000, company);        ScrumMaster SM = new ScrumMaster("Anel", 26, 'F', 3, 145000, company);        Tester tester1 = new Tester("Chinara", 32, 'F', 11, "QA", 125000, company);        Tester tester2 = new Tester("Yasaman", 31, 'F', 12, "QE", 130000, company);        Tester tester3 = new Tester("Irena", 29, 'F', 13, "SDET", 128000, company);        Tester tester4 = new Tester("Cihad", 26, 'M', 14, "QA", 125000, company);        Tester[] testers = {tester1, tester2, tester3, tester4};        Developer developer1 = new Developer("Max", 40, 'M', 32, "Senior Java Developer", 180000, company);        Developer developer2 = new Developer("Allison", 18, 'F', 33, "Junior Java Developer", 120000, company);        Developer developer3 = new Developer("Tom", 26, 'M', 35, "Java Developer", 140000, company);        Developer developer4 = new Developer("Manas", 40, 'M', 38, "Full Stack Developer", 280000, company);        Developer developer5 = new Developer("Evelyn", 30, 'F', 40, "JS Developer", 160000, company);        Developer[] developers = {developer1, developer2, developer3, developer4, developer5};        ScrumTeam team = new ScrumTeam(PO, BA, SM);        System.out.println(team);        System.out.println("------------------------------------");        team.addDeveloper(developer1);        team.addTester(tester3);        System.out.println(team);        System.out.println("------------------------------------");        team.addDevelopers(developers);        team.addTesters(testers);        System.out.println(team) ;        System.out.println("------------------------------------");        for (Tester eachTester:team.testers){            System.out.println(eachTester.name+" : "+eachTester.salary);        }        System.out.println("------------------------------------");        for (Developer devs:team.developers){            System.out.println(devs.name+" : "+devs.salary);        }    }}