package day38_OOP_Inheritance_MethodOverloading.areaTask;public class Circle extends Shape  {    public double r;    public static double pi=3.14;    public Circle(String name, double r) {        super(name);        this.r = r;    }    @Override    public double perimeter() {        return 2*r*pi;    }    @Override    public double area() {        return r*r*pi;    }    public String toString() {        return "Circle{" +                "name='" + name + '\'' +                ",radius=" + r +                ",pi=" + pi +                ",area=" + area() +                ",perimeter=" + perimeter() +                '}';    }}/*Circle	r	pi	toString(): name, r, pi, area, perimeter*/