package day38_OOP_Inheritance_MethodOverloading.areaTask;public class Rectangle extends Shape {    public double length;    public double width;    public Rectangle(String name, double length, double width) {        super(name);        this.length = length;        this.width = width;    }    @Override    public double area() {        return width*length;    }    @Override    public double perimeter() {        return 2*(width+length);    }    public String toString() {        return "Rectangle{" +                "name='" + name + '\'' +                ",length=" + length +                ", width=" + width +                ",area=" + area() +                ",perimeter=" + perimeter() +                '}';    }}/*Rectangle	l	w	toString(): name, l, w, area, perimeter*/