package day38_OOP_Inheritance_MethodOverloading.areaTask;public class Square extends Shape {    public double side;    public Square(String name, double side) {        super(name);        this.side = side;    }    @Override    public double perimeter() {        return side*4;    }    @Override    public double area() {        return side*side;    }    @Override    public String toString() {        return "Square{" +                "name='" + name + '\'' +                ",side=" + side +                ",area=" + area() +                ",perimeter=" + perimeter() +                '}';    }}/*Square	s	toString(): name, s, area, perimeter*/