package day39_Recap.areaTask;public class Circle extends Shape {    private double radius;    public static double pi = 3.14;    public double getRadius() {        return radius;    }    public void setRadius(double radius) {        if (radius <= 0) {            System.out.println("Side can't be zero or negative number..");            System.exit(0);        }        this.radius = radius;    }    public Circle(double radius) {        super("Circle");        setRadius(radius);    }    @Override    public double area() {        return radius * radius * pi;    }    @Override    public double perimeter() {        return 2 * radius + pi;    }    public String toString() {        return "Circle{" +                "pi=" + pi +                ", radius=" + radius +                ", area=" + area() +                ", perimeter=" + perimeter() +                '}';    }}/*Circle extends Shape:		variables:			radius			pi (static)		Encapsulate the field		Add a constructor to set the filed		area(): radius * radius * pi		perimeter(): 2 * radius * pi		toString(): r, pi, area, perimeter*/