package day39_Recap.areaTask;public class Rectangle extends Shape {    private double length;    private double width;    public double getLength() {        return length;    }    public void setLength(double length) {        this.length = length;        if (length <= 0) {            System.out.println("Side can't be zero or negative number..");            System.exit(0);        }    }    public double getWidth() {        return width;    }    public void setWidth(double width) {        if (width <= 0) {            System.out.println("Side can't be zero or negative number..");            System.exit(0);        }        this.width = width;    }    public Rectangle(double length, double width) {        super("Rectangle");        setLength(length);        setWidth(width);    }    @Override    public double area() {        return width * length;    }    @Override    public double perimeter() {        return 2 * (width + length);    }    public String toString() {        return "Rectangle{" +                "length=" + length +                ", width=" + width +                ", area=" + area() +                ", perimeter=" + perimeter() +                '}';    }}/*Rectangle extends Shape:	variables:		length		width	Encapsulate the fields	Add a constructor to set the filed	area(): length * width	perimeter(): 2 * ( width + length)	toString(): length, width, area, perimeter*/