package day39_Recap.cydeoTask;public class Employee extends Person{    private int employeeId;    private String jobTitle;    private double salary;    public int getEmployeeId() {        return employeeId;    }    public void setEmployeeId(int employeeId) {        if (employeeId<=0||employeeId>100){            System.out.println("EmployeeId is between 1-100 !");            System.exit(1);        }        this.employeeId = employeeId;    }    public String getJobTitle() {        return jobTitle;    }    public void setJobTitle(String jobTitle) {        this.jobTitle = jobTitle;    }    public double getSalary() {        return salary;    }    public void setSalary(double salary) {    if (salary<35000){        System.out.println("anyone will not work lower than 35000$");        System.exit(1);    }        this.salary = salary;    }    public Employee(String name, int age, char gender, int employeeId, String jobTitle, double salary) {        super(name, age, gender);        this.employeeId = employeeId;        this.jobTitle = jobTitle;        this.salary = salary;    }    public void work(){        System.out.println("Employee working on his/her own job");    }    public String toString() {        return "Employee{" +                "name='" + getName() + '\'' +                ", age=" + getAge() +                ", gender=" + getGender() +                "employeeId=" + employeeId +                ", jobTitle='" + jobTitle + '\'' +                ", salary=" + salary +                '}';    }}/*2. Create a sub class of Person named Employee:            Extra variables:                employeeId, jobTitle, salary            Encapsulate all the fields            Add a constructor to set all the fields            Methods:                work()                toString()*/