package day40_FinalKeyword;import day38_OOP_Inheritance_MethodOverloading.areaTask.Circle;import day39_Recap.areaTask.Square;public class EncapsulationReview {    private Circle circle;    private Square square;    public Circle getCircle() {        return circle;    }    public void setCircle(Circle circle) {       if (circle.r<5){           System.out.println("Radius smaller than 5");           System.exit(1);       }        this.circle = circle;    }    public Square getSquare(){        return square;    }    public void setSquare(Square square){        if (square.getSide()<3){            System.out.println("Square side must be bigger than 3");            return;        }        this.square=square;    }}